mapper.select:
SELECT o.ID, o.BANK_IP, o.BANK_PORT, o.BANK_SEND, o.BANK_SEND_TIME, o.GAS_ECHO, o.GAS_ECHO_TIME, t.typename BANK_COMMAND, o.STATE, o.CREATE_BY, o.CREATE_DATE, GAS_ECHO_TIME_MILLIS - BANK_SEND_TIME_MILLIS TIME_INTERVAL FROM mid_operation o JOIN t_s_type t ON o.BANK_COMMAND = t.typecode
o.UPDATE_DATE >= #{startTime, jdbcType=DATE}
AND o.UPDATE_DATE <= #{endTime, jdbcType=DATE}
AND t.TYPECODE = #{bankCommand}
ORDER BY CREATE_DATE DESC
